def fibonacci(n): 
    # Base case: 0th Fibonacci number is 0
    if n == 0: 
        return 0
    # Recursive case: 1st Fibonacci number is 1 and 2nd Fibonacci number is 1 + 0 = 1
    elif n == 1: 
        return 1
    # Recursive case: n-1th Fibonacci number is (n-1)th Fibonacci number + previous Fibonacci number
    else: 
        return fibonacci(n-1) + fibonacci(n-2) 

