class Queue: 
    def __init__(self): 
        self.front = None
        self.back = None

    def enqueue(self, x): 
        if self.front is None: 
            self.front = self.back = None
        else: 
            self.back = None
        self.front = x 

    def dequeue(self): 
        if self.front is not None: 
            self.front = self.front.next
        return self.front 

    def is_empty(self): 
        return self.front is None
