string html_format(string md_text) {
    std::string html_string = "<html><body>";

    for (auto& c : md_text) {
        if (c == '\n') {
            html_string += "<br>";
        } else if (c == '[' || c == ']') {
            html_string += "<strong>";
        } else if (c == '*') {
            html_string += "<em>";
        } else if (c == '_') {
            html_string += "<i>";
        } else if (c =='') {
            html_string += "&nbsp;";
        } else {
            html_string += c;
        }
    }
    html_string += "</body></html>";
    return html_string;
}

