def longest_common_subsequence(s1, s2):
    seq1 = []
    seq2 = []
    for c in s1:
        seq1.append(c)
    for c in s2:
        seq2.append(c)
    lcs = [0] * (len(seq1) + 1)
    for i in range(len(seq1)):
        lcs[i] = max(lcs[i], len(seq2[i]) + 1)
    for i in range(len(seq2)):
        lcs[i] = max(lcs[i], len(seq1[i]) + 1)
    for i in range(len(seq1), len(seq1) + len(seq2)):
        lcs[i] = min(lcs[i - len(seq2)], lcs[i - len(seq1)])
    return lcs[len(seq1) - len(seq2)]
