class Queue:
    def __init__(self):
        self.front = []
        self.back = []
    def enqueue(self, x):
        self.front.append(x)
        if self.front:
            self.back = self.front[:]
        return self
    def dequeue(self):
        if self.front:
            return self.front.pop()
        else:
            return None
    def empty(self):
        return self.front == [] and self.back == []
