void convertMarkdownToHtml(std::string& markdown, std::string& html) 
{ 
    // Converting markdown to html 
    // http://www.mercurysoftware.com/wiki/Markdown_to_HTML
    // In this example we use a regular expression to extract the markdown syntax and replace it with a appropriate html. 
    // We also replace tabs with spaces and remove any line breaks at the end of the string. 
    // Finally we replace all remaining characters with a space. 
    // Note that this is a very basic implementation of markdown to html conversion and may need further refinement. 
    markdown = markdown.replace("\\*", "<strong>");
    markdown = markdown.replace("_", " ");
    markdown = markdown.replace("\\{", "<em>");
    markdown = markdown.replace("\\}", "</em>");
    markdown = markdown.replace("\\[", "<a href=\"#\">");
    markdown = markdown.replace("\\]", "</a>");
    markdown = markdown.replace("\\(", "");
    markdown = markdown.replace("\\)", "");
    markdown = markdown.replace("\\'", "&#39;");
    markdown = markdown.replace("\\<", "");
    markdown = markdown.replace("\\>", "");
    html = markdown;
    html = html.replace("\n", "");
    html = html.replace(" ", "");
    html = html.replace("  ", " ");
}

