import re 

def validate_email(email_address):
    pattern = re.compile(r"^[\w\.\+\-]+\@[\w]+\.[a-z]{2,3}$")
    return pattern.match(email_address) is not None

if __name__ == '__main__':
    print(validate_email('example@example.com'))

def findFibonacci(n): 
    if n==0: 
        return 0 
    elif n==1: 
        return 1
    else: 
        return findFibonacci(n-1)+findFibonacci(n-2) 

def main(): 
    print(findFibonacci(5))

def binarySearch(arr, target): 
    low = 0
    high = len(arr) - 1

    while (low <= high): 
        mid = int((low + high)/2)
        if arr[mid] == target: 
            return mid
        else if arr[mid] < target: 
            low = mid + 1
        else: 
            high = mid - 1

    return -1 

if __name__ == '__main__':
    arr = [5, 15, 25, 35, 45]
    target = 35

    print(binarySearch(arr, target))
